INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'В ноябре 1850 года француженку, с которой Александр Васильевич Сухово-Кобылин (1817–1903) прожил восемь лет в тайной любовной связи, находят зверски убитой в сугробах Ходынского поля под Москвой. Начинается уголовное следствие, и улики указывают на него – молодого и богатого барина, известного аристократа, игрока и донжуана, будущего автора “Свадьбы Кречинского”, “Дела” и “Смерти Тарелкина”, чье творчество развивалось под прямым и скрытым влиянием обвинения в тяжком преступлении.
