INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Накануне коронации Александра III молодая баронесса Амалия Корф отправилась навестить дальних родственников своего мужа. Вначале ей очень понравился их ухоженный, словно кукольный, особняк, но позже Амалия стала свидетельницей нескольких весьма неприятных семейных сцен и поняла, что в доме не все так гладко, как хотят показать его обитатели. А вскоре она узнала, что Наталья Дмитриевна, почтенная мать семейства, ушла на прогулку и не вернулась, а в ближайшем лесу была найдена ее окровавленная перчатка. Подозреваемых предостаточно – это и муж, имеющий семью на стороне, и его сын от первого брака, ненавидящий мачеху… Амалия была почти уверена в разгадке, но события вдруг приняли совершенно неожиданный оборот…',
