INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'От автора – лауреата множества престижных литературных премий, чьи произведения опубликованы в 28 странах, на все книги писательницы приобретены права на экранизацию. Два года в топ-10 по всему миру! 10 000 восторженных откликов на Amazon.com! Абсолютная книжная сенсация последних лет!
Все было готово для празднования пятилетнего юбилея супружеской жизни, когда вдруг необъяснимо пропал один из виновников торжества. Остались следы борьбы в доме, кровь, которую явно пытались стереть,&nbsp;– и цепочка «ключей» в игре под названием «охота за сокровищами»; красивая, умная и невероятно изобретательная жена ежегодно устраивала ее для своего обожаемого мужа.
И похоже, что эти «ключи» – размещенные ею тут и там странные записки и не менее странные безделушки – дают единственный шанс пролить свет на судьбу исчезнувшей. Вот только не придется ли «охотнику» в процессе поиска раскрыть миру и пару-тройку собственных малосимпатичных тайн?',
        0, 80, 0, 0, 500, '2023-06-07', '6ddaff7d-40e4-413e-9438-a1a0cb72f6bb', 'Исчезнувшая');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Один из самых известных романов мэтра детективного жанра во Франции.
Лиза-Роза или Эмили? Как зовут трехмесячную малышку, единственную, кто выжил после падения самолета, совершавшего рейс по маршруту Стамбул – Париж? Две семьи, одна богатая, а другая нет, оспаривают друг у друга спасшуюся девочку… Спустя восемнадцать лет частный сыщик утверждает, что распутал эту давнюю историю, но вскоре его находят мертвым. Однако осталась тетрадь, куда детектив записывал детали своего расследования. Сюжет, больше похожий на детективную гонку, движется из Парижа в&nbsp;Альпы, с горных склонов в&nbsp;Стамбул и обратно – до тех пор, пока наконец не спадут маски. Разворачивающаяся драма – дело случая или в этой пьесе с самого начала кто-то манипулировал всеми персонажами?',
        0, 81, 1, 0, 500, '2023-06-07', '23cf872e-03c7-421a-ae65-14b548db580c', 'Самолет без нее');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Летом во время отпуска частный сыщик Игорь Гладышев и его сослуживец Владислав Русанов едут на несколько дней на рыбалку на водохранилище, расположенное неподалеку от города Белозерска. С ними на остров отправляются еще пять человек. И вот в самый разгар рыбной ловли, из ружья убивают зятя Владислава Владимира Петровича Жилина. Кроме шестерых человек на острове никого больше нет. Значит, убийца среди рыбаков. Под подозрением у Игоря оказываются пять человек, но все они были друг у друга на виду, ни у кого не было ружья, а человек убит. Кто этот хитроумный убийца, каким образом ему удалось совершить преступление, и за что он убил Владимира Жилина? На эти вопросы предстоит ответить Игорю во время расследования этого загадочного преступления…',
        0, 82, 0, 0, 500, '2023-06-07', '4767494c-d1f0-47f3-b7b0-650c2f487217', 'Драма на острове');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Когда нам девятнадцать, мы не думаем, что девочка, которую мы целуем сегодня, возможно, единственная из всего мира способна дотянуться до сердца, согреть его и забрать – незаметно, неумолимо. Мы уходим, глядя за горизонт, не оглядываясь, не сомневаясь, уверенные, что там, в будущем, нас ждут новые победы и свершения, красивые женщины и успех. Но только эта девочка из вчера, из крошечного убогого города не перестает жить в нашем сердце, и не имеет никакого значения, сколько пройдет лет – ничего не изменится. Она будет там. Но уже не моя.
Содержит нецензурную брань.', 0, 83, 0, 0, 500, '2023-06-07', '17c7571d-90ae-4022-9c6e-c8384b981c0e',
        'По ту сторону от тебя');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Продолжение бестселлера «Жена убийцы», проданного в России тиражом более 20 000 экземпляров. Высокие рейтинги книги на Amazon и Goodreads. Гремучая смесь Майка Омера и Рейчел Кейн. Книга, написанная профессионалом, побывавшим по обе стороны уголовного правосудия – на месте и прокурора, и адвоката.
Одержимый картинами загадочного африканского художника, он воплощает их в своих жестоких убийствах. Чтобы поймать маньяка, нужно понять изощренный смысл этих картин…
Черные туники. Туго обмотанные вокруг головы бинты, насквозь пропитанные кровью. Ногти обрезаны, волосы острижены, на коже следы отбеливателя. Так выглядят жертвы убийцы, прозванного Палачом с Багряного озера…
Фанат безумного кенийского художника Сарпонга, он воплощает в реальность его кровавый цикл «Ночные твари». Никто не знает, какой смысл вкладывал Сарпонг в свои холсты. Но маньяк явно видит в них некую идею. Какую же?
Чтобы это понять, прокурор Джессика Ярдли должна обратиться за помощью к бывшему мужу – ждущему казни серийному убийце. Тот не только знаток психологии психопатов, он сам художник. Но пойти на такое – выше ее сил… Тем временем убийца готовится «очеловечить» очередную картину. Героиней которой вполне может стать сама Джессика…
«Мрачная книга, мрачная. И еще раз мрачная. Но тех, кто откроет ее, ждет незабываемая история».&nbsp;– Kirkus Reviews',
        0, 84, 0, 0, 500, '2023-06-07', '03013753-2523-47e6-ac5a-eb20e72f7483', 'Ночные твари');
