INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Раненый минометчик ЧВК «Вагнер» приходит в себя в госпитале. Его друзья погибли в Донбассе, выжил только он. Его подозревают в предательстве, а он из-за частичной амнезии не может доказать невиновность и корит себя в смерти друзей. Теперь он должен найти и разоблачить предателя, иначе его ликвидируют.
«Достоверный и искренний роман популярного писателя Сергея Бакшеева – первое литературное произведение о ЧВК «Вагнер» (Журнал «Подвиг»).',
