INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Эта пьеса «Победа» посвящается Дню победы в&nbsp;Великой Отечественной войне, написана в&nbsp;1992—1994&nbsp;гг. моим покойным, любимым дедушкой, которого зовут Арслан Юсупович Юсупов. Его псевдоним Гали Арслан. На&nbsp;протяжении своей жизни он написал много рукописей, поэм, стихов, которые мне завещал и&nbsp;просил, чтобы я поделилась ими с&nbsp;вами, дорогие читатели. С&nbsp;большим уважением к&nbsp;Вам, Восток Лейла&nbsp;Эл. leylaleylochka@mail.ru',
