INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Казалось бы, что может быть общего между стильной танцовщицей хип-хопа и скромной киргизской девушкой, чьи родители работают в ДЭЗе? Крутым сорокалетним хедхантером и парой юных молодоженов-студентов? Или ненавидящей свою работу секретаршей-толстушкой и мужчиной в кризисе среднего возраста, разрывающимся между семьей и любовницей? Но однажды каждый из них находит среди книг на полке буккроссинга рукописную зеленую тетрадь – и эта тетрадь неожиданно меняет всю его жизнь…',
        0, 101, 0, 0, 500, '2023-06-07', '05292372-46d0-4e19-9b5b-dc5cf729bdcf', 'Зеленая тетрадь');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Элинор&nbsp;– идеальная жена и мать двоих малышей&nbsp;– готовится отметить десятую годовщину семейной жизни, но вместо законного супруга застает в гостиной таинственного незнакомца, утверждающего, что он и есть ее муж. К ужасу Элинор, его слова подтверждают общие знакомые и близкие люди. Самое чудовищное в этом безумии&nbsp;– Лин пытаются убедить в том, что у нее никогда не было детей и три последних года она провела в своей мастерской, рисуя портреты мужа…
<i>Содержит нецензурную брань</i>', 0, 102, 0, 0, 500, '2023-06-07', '1b13f050-0b93-4c0a-9f24-241e3ba132da', 'Не он');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Шесть ответственных взрослых. Три милых ребенка. Одна маленькая собака. Обычный уик-энд. Что же могло пойти не так?
Вид и Тиффани приглашают соседей и их друзей на барбекю. И все с радостью соглашаются в надежде провести приятный вечер в красивом доме. Вкусная еда, приготовленная хозяином, остроумные шутки, непринужденная беседа, легкий флирт. Неожиданно раздается жуткий крик: двухлетняя девочка упала в бассейн фонтана и едва не утонула. И это событие резко меняет жизнь, казалось бы, счастливых и благополучных семей. Каждого мучает чувство вины, и в результате их брак трещит по швам, давняя дружба проверяется на прочность, в каждом невинном поступке усматривается злой умысел…
<i>Впервые на русском языке!</i>', 0, 104, 0, 0, 500, '2023-06-07', 'ae71fd31-75bb-4c00-96c5-f51cc41b45e1',
        'Верные, безумные, виновные');
