INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'«Все не так» – детективная сага о большой семье. Маринина рассказывает о «дружной» семье, в которой каждый ее член пытается урвать кусок побольше, старается использовать других и управлять ими так, чтобы никто не догадался об истинных целях.
Всех представителей семьи обуревают потаенные страсти и запретные желания. Поэтому, когда погибает один из членов семейства, все домочадцы попадают под подозрение…',
