INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'–&nbsp;Почему сразу не сообщила?&nbsp;– киваю на близнецов, спящих в люльках.&nbsp;– Я вроде тоже имею отношение…
Обычно с выдержкой у меня полный порядок. Но сейчас жестко плющит от обиды. Зачем ты так, Лерочка?
–&nbsp;Откуда…&nbsp;– тянет растерянно.
–&nbsp;У меня везде свои люди,&nbsp;– перебиваю резко.&nbsp;– Думала, не узнаю? Серьезно?
–&nbsp;Ты же сразу предупредил, что продолжения не будет.&nbsp;– шепчет потупясь.
В груди давит от гнева. Даже дышать тяжело. Дети мои, и я не собираюсь от них отказываться.', 0, 132, 0, 0, 500,
        '2023-06-07', '1107983f-7d5c-4bed-82bb-9cb5ca7423b3', 'Двойня в подарок');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Молодая супружеская пара – Поль, музыкальный продюсер, и Мириам, начинающий адвокат,&nbsp;– нанимает детям няню – вдову средних лет, у которой есть живущая отдельно взрослая дочь. Поль и Мириам приятно удивлены тем, насколько им подошла Луиза: она завоевала сердца детей, изобретая все новые увлекательные игры, и стала поваром и домоправительницей. Никогда еще в квартире не было так чисто, а на столе – так много вкусной еды. Но со временем супруги обнаруживают, что попали в зависимость от своей идеальной няни…
Получившая в 2016 году Гонкуровскую премию, книга Лейлы Слимани – захватывающий психологический триллер и в то же время списанная с самой жизни история сложных человеческих отношений, где любовь, отравленная ревностью, превращается в ненависть.',
        0, 133, 0, 0, 500, '2023-06-07', '5d87e6f6-f27f-4188-8b02-88b7b6920cc2', 'Идеальная няня');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Семейная трагедия, история несбывшейся любви и исповедь об утраченной юности и надеждах.
Словно потерянный рай, Бумажный дворец пленит, не оставляя в покое. Бумажный дворец – место, которое помнит все ее секреты. Здесь она когда-то познала счастье. И здесь, она утратила его навсегда. Элла возвращается туда снова и снова, ведь близ этих прудов и тенистых троп она потеряла то, что, казалось, уже не вернуть.
Но один день изменит все.
Привычная жизнь рухнет, а на смену ей придет неизвестность – пугающая и манящая, обещающая жизнь, которую она так долго не решалась прожить… Осталось сделать только один шаг навстречу.',
        0, 134, 0, 0, 500, '2023-06-07', '1c625e5f-c686-4f19-bbe1-2122ecf59251', 'Бумажный дворец');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Десять лет назад детективу Ионе Колли едва удалось пережить бесследное исчезновение четырехлетнего сына Тео.
Теперь он, случайно обнаружив четыре искалеченных трупа, атакованный неизвестным преступником и чудом выживший, с изумлением обнаруживает, что стал главным подозреваемым по делу об этих жестоких убийствах. И, что еще ужаснее, теперь его подозревают в том, что он убил собственного ребенка!
Иона догадывается: по неизвестным причинам его расчетливо и жестоко подставляют.
Доказать свою невиновность он может, только лично схватив настоящего убийцу и одновременно выяснив, что же произошло с Тео…',
        0, 135, 0, 0, 500, '2023-06-07', 'bf058a7e-7a2f-4fc3-9300-80c8126492f1', 'Исчезнувшие');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('У современных золушек нет феи-крестной. Выбираться из нищеты и отвоевывать свое место под солнцем они вынуждены сами. Поэтому и Мия взяла судьбу в собственные руки. Тщательно спланированное знакомство с олигархом Валентином Гестом прошло успешно, однако дальше вместо райской жизни Мию подстерегали очередные проблемы. Где же он, долгожданный принц?..',
