INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('В романе «Эр Тёштюк и утраченный Джан», написанном в жанре фэнтези, используются мотивы одноименного киргизского эпоса, содержатся ссылки на шумерскую, греческую, римскую, скандинавскую мифологии. По желанию бая во время выборов Колдун обеспечивает ему выигрыш. За эту услугу он, превратив его душу в щепотку земли, забирает её с собой. Чтобы спасти жизнь отца, Тёштюк оказывается в Нижнем мире, выдерживает все постигшие его испытания.',
