INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Дилогия «Дневник революции-2» – продолжение фантастической антиутопии, увидевший свет в 2019 году. Борьба против диктатуры могущественной организации «Единое Око» в системе Сапей не окончена. Участникам движения сопротивления – «истинным союзникам» Аску и Сольвейг предстоят новые испытания, главным из которых станет преодоление собственных заблуждений.
В отличие от авторов классических антутопий, София Эззиати уделяет наибольшее внимание не описанию причудливого мира будущего, а внутренним переживаниям героев, проблемам нравственного выбора и этическим дилеммам – как на личном, так и на глобальном уровне. Почему люди совершают предательства? Какую опасность таит в себе совершенный искусственный интеллект? И что делать, если власть борцов с тоталитарным режимом оборачивается ещё более жёсткой диктатурой? На эти и другие вопросы ищет ответ автор дилогии.
В центре романа – путь человека, пытающегося обрести свою подлинную сущность и подлинную свободу. И надеющегося на то, что добро в конечном итоге все-таки победит зло.',
        0, 189, 0, 0, 500, '2023-06-07', '13b52f3d-1352-48ca-bb18-dbd2bad43dc3', 'Дневник революции 2. Дилогия');
