INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Январь 1939&nbsp;года. Германия. Страна, затаившая дыхание. Никогда еще у&nbsp;смерти не&nbsp;было столько работы. А&nbsp;будет еще больше.
Мать везет девятилетнюю Лизель Мемингер и&nbsp;ее младшего брата к&nbsp;приемным родителям под&nbsp;Мюнхен, потому что их отца больше нет&nbsp;– его унесло дыханием чужого и&nbsp;странного слова «коммунист», и&nbsp;в&nbsp;глазах матери девочка видит страх перед такой&nbsp;же судьбой. В&nbsp;дороге смерть навещает мальчика и&nbsp;впервые замечает Лизель.
Так&nbsp;девочка оказывается на&nbsp;Химмель-штрассе&nbsp;– Небесной улице. Кто&nbsp;бы ни&nbsp;придумал это название, у&nbsp;него имелось здоровое чувство юмора. Не&nbsp;то чтобы там была сущая преисподняя. Нет. Но&nbsp;и&nbsp;никак не&nbsp;рай.
«Книжный вор»&nbsp;– недлинная история, в&nbsp;которой, среди прочего, говорится: об&nbsp;одной девочке; о&nbsp;разных словах; об&nbsp;аккордеонисте; о&nbsp;разных фанатичных немцах; о&nbsp;еврейском драчуне; и&nbsp;о&nbsp;множестве краж. Это&nbsp;книга о&nbsp;силе слов и&nbsp;способности книг вскармливать душу.',
        0, 265, 1, 0, 500, '2023-06-07', '35aceddb-a421-40d8-9bae-47d71ab98ecc', 'Книжный вор');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Вы когда-нибудь мечтали стать героем? Спасти из горящего дома девушку, вытащить из воды ребенка, совершить отчаянный, но благородный поступок? Эд Кеннеди ни о чем подобном не мечтал. Он просто жил, иногда играл в карты, работал. И так бы и продолжалось, если бы однажды Эд не сорвал дерзкое ограбление банка.
Вот тут-то и пришлось ему сделаться посланником. Но кто его выбрал на эту роль и с какой целью?
Впрочем, привычка плыть по течению пригодилась Эду и здесь: он безропотно ходит от дома к дому и приносит кому пользу, а кому и вред – это уж как решит избравшая его своим орудием безымянная и безликая сила. Каждая выполненная миссия оставляет в его судьбе неизгладимый след, но приближается ли разгадка тайны?',
        0, 266, 0, 0, 500, '2023-06-07', '195a4d97-4b82-4650-8c15-293ef2538e41', 'Я – посланник');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Пять братьев Данбар жили в идеальном хаосе своего дома – без родителей. Пока однажды вдруг не вернулся отец, который когда-то их оставил. У него странная просьба – он хочет, чтобы сыновья согласились построить с ним мост.
Откликается Клэй, мальчик, терзаемый давней тайной.
