INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'“Правда о деле Гарри Квеберта” вышла в 2012 году и сразу стала бестселлером. Едва появившись на прилавках, книга в одной только Франции разошлась огромным тиражом и была переведена на тридцать языков, а ее автор, двадцатисемилетний швейцарец Жоэль Диккер, получил Гран-при Французской академии за лучший роман и Гонкуровскую премию лицеистов.
Действие этой истории с головокружительным сюжетом и неожиданным концом происходит в США. Молодой успешный романист Маркус Гольдман мается от отсутствия вдохновения и отправляется за помощью к своему учителю, знаменитому писателю Гарри Квеберту. Однако внезапно выясняется, что помощь требуется самому Гарри, обвиненному в убийстве, которое произошло в тихом американском городке 33 года назад. Чтобы спасти Гарри от электрического стула, Маркус берется за собственное расследование и пытается распутать сложнейший клубок лжи, давно похороненных тайн и роковых случайностей. И получает тридцать один совет, как написать бестселлер.',
        0, 277, 0, 0, 500, '2023-06-07', '0a789c57-e840-4037-9a80-019ca7380a12', 'Правда о деле Гарри Квеберта');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'1969 год. Год полета первого человека на Луну! И ничто не может помешать состояться этому событию, потому что могущественная армия США готова моментально уничтожить любую помеху всеми возможными способами, включая ядерное оружие. Но, оказывается, есть что-то, что до настоящего времени покоилось под толщей Атлантического океана многие тысячи лет и, возможно, имеющее отношение к прошлогодним событиям, начавшимся под городским пляжем и переместившимся в город, едва не уничтожив половину Корал-Гейблс. В самый эпицентр смертельной заварушки попадает биоробот, которого пытаются выкрасть с секретной военной базы в Неваде двое морских пехотинцев США и Майк Деррик. У заговорщиков есть всего несколько дней, чтобы предотвратить самое страшное, что только можно вообразить.
Эта невероятная история возможно и была, но нам о ней вряд ли бы кто рассказал. Она была надежно засекречена, ведь она являлась частью большой межгалактической политической игры, едва не стершей с лица Земли ее коренное население. Спросите – кто на самом деле правит нашим миром? Вы это сможете узнать, прочитав фантастическую книгу «Маркус Фастмувер. Вторжение»!',
        0, 278, 0, 0, 500, '2023-06-07', 'b8f7ca0e-1f77-4c0d-913a-3733f74f2d0c', 'Маркус Фастмувер. Вторжение');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'После “Правды о деле Гарри Квеберта”, выдержавшей тираж в несколько миллионов и принесшей автору Гран-при Французской академии и Гонкуровскую премию лицеистов, новый роман тридцатилетнего швейцарца Жоэля Диккера сразу занял верхние строчки в рейтингах продаж.
В “Книге Балтиморов” Диккер вновь выводит на сцену героя своего нашумевшего бестселлера&nbsp;– молодого писателя Маркуса Гольдмана. В этой семейной саге с почти детективным сюжетом Маркус расследует тайны близких ему людей. С детства его восхищала богатая и успешная ветвь семейства Гольдманов из Балтимора. Сам он принадлежал к более скромным Гольдманам из Монклера, но подростком каждый год проводил каникулы в доме своего дяди, знаменитого балтиморского адвоката, вместе с двумя кузенами и девушкой, в которую все три мальчика были без памяти влюблены. Будущее виделось им в розовом свете, однако завязка страшной драмы была заложена в их историю с самого начала.',
        0, 279, 0, 0, 500, '2023-06-07', '995bec76-9b0f-4073-b51f-7ca83fee3f40', 'Книга Балтиморов');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Даже самая несложная работа требует таланта. Думать, что любого можно научить выполнять любую работу и что дело только в системе стимулирования и амбициях – значит путать таланты, знания и навыки. Талант – вещь врожденная, знания и навыки – дело наживное. Книга Маркуса Бакингема и Курта Коффмана подводит итог грандиозной двадцатипятилетней работы Института Гэллапа, суть которой сводится к тому, что людей изменить невозможно и не стоит требовать от них того, что им не дано. Нужно выявлять их существующие таланты и помогать раскрывать сильные стороны. Совпадение способностей человека и роли, которую он выполняет,&nbsp;– вот к чему должен стремиться каждый менеджер.
Книга предназначена для всех практикующих менеджеров.
<i>5-е издание.</i>', 0, 280, 0, 0, 500, '2023-06-07', '5dfcc731-04b3-4889-b517-652bd832ce36',
        'Сначала нарушьте все правила. Что лучшие в мире менеджеры делают по-другому');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'451° по Фаренгейту – температура, при которой воспламеняется и горит бумага. Философская антиутопия Брэдбери рисует беспросветную картину развития постиндустриального общества: это мир будущего, в котором все письменные издания безжалостно уничтожаются специальным отрядом пожарных, а хранение книг преследуется по закону, интерактивное телевидение успешно служит всеобщему оболваниванию, карательная психиатрия решительно разбирается с редкими инакомыслящими, а на охоту за неисправимыми диссидентами выходит электрический пес… Роман, принесший своему творцу мировую известность.
