INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Самые известные произведения «старого доброго сказочника» Рэя Брэдбери в одном томе. Любимый писатель миллионов умер совсем недавно – 5 июня 2012 года, но он сделал мир намного лучше, оставив нам красные пески и каналы Марса, человека в картинках, Хэллоуин на Среднем Западе, маленькие городки и темные карнавалы.
Мастер мирового масштаба, совмещающий в литературе несовместимое. Создатель таких ярчайших шедевров, как «Марсианские хроники», «451 по Фаренгейту», «Вино из одуванчиков» и так далее и так далее. Лауреат многочисленных премий. Это Рэй Брэдбери. Его увлекательные истории прославили автора не только как непревзойденного рассказчика, но и как философа, мыслителя и психолога. Магический реализм его прозы, рукотворные механизмы радости, переносящие человека из настоящего в волшебные миры детства, чудо приобщения к великой тайне Литературы, щедро раздариваемое читателю, давно вывели Брэдбери на высокую классическую орбиту. Собранные в этой книге произведения достойное тому подтверждение.',
        0, 356, 0, 0, 500, '2023-06-07', 'ceca319d-9d0b-4f6a-af53-c3607cc1cbfb',
        '451° по Фаренгейту. Повести. Рассказы');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'«Кладбище для безумцев: Еще одна повесть о двух городах» – второй роман в условной трилогии, к которой также относятся «Смерть – дело одинокое» и «Давайте все убьем Констанцию». Снова действие происходит в Голливуде, снова ближайшей жанровой аналогией – хотя отнюдь не исчерпывающей – будет детектив-нуар.
Ровно в полночь во время хеллоуинской вечеринки на примыкающем к легендарной студии легендарном кладбище главный герой (писатель-фантаст и сценарист, альтер эго самого Брэдбери) видит студийного магната, погибшего в такую же ночь Хеллоуина двадцать лет назад. Отыскать выход из лабиринта смертей, реальных и мнимых, герою помогают надменный австрийско-китайский режиссер с неизменным моноклем и бальзамировщик мумии Ленина, иисусоподобный забулдыга по имени Иисус Христос и лучший в мире аниматор динозавров.',
