INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Знаменитая киноактриса Констанция Раттиган получает пренеприятную посылку: записную книжку с телефонами давно умерших. Есть чему пугаться, ведь имена немногих оставшихся в живых помечены жирным крестом, в том числе и фамилия самой Констанции. Чтобы спасти ее и раскрыть загадку последовавшей за этим цепочки смертей, потрясших Голливуд воспоминаний и Голливуд современности, героям нового романа Брэдбери (уже знакомым читателю по произведению «Смерть – дело одинокое») придется Констанцию убить. Причем всем вместе.',
