INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Этот сборник – отличное доказательство того, что мы еще не открыли все наследие Великого Мастера. О чем бы Брэдбери ни писал: о Марсе, Диком Западе, роботах или полетах в космос,&nbsp;– он всегда пишет о самом близком и дорогом: о детстве, памяти, фантазиях, доброте.
Впервые изданные на русском рассказы и стихотворения не только проводят контур по всей творческой биографии писателя, но и дают нам заглянуть внутрь его воображения. Менялся мир, менялись страхи, мечты и предубеждения, и Рэй Брэдбери с чуткостью художника улавливал эти изменения. Менялся мир – вместе с Брэдбери, благодаря ему.',
        0, 367, 0, 0, 500, '2023-06-07', 'c91b774a-6ded-4f57-abce-be4fa0c97c05', 'Механический хэппи-лэнд (сборник)');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Рэй Брэдбери – великий мастер пера, классик мировой литературы и живая легенда фантастики. Его книги уже более полувека увлеченно читают миллионы людей во всем мире. Брэдбери намного больше, чем писатель-фантаст,&nbsp;– его творчество можно отнести к «высокой», внежанровой литературе.
Такого дерева вы не видели никогда в жизни. Оно достигает вершиной небес, и растут на нем тыквы всех форм, размеров и цветов. Возле этого дерева вы встречаете странного господина по имени мистер Смерч, и он увлекает вас в чудесное путешествие, из которого вы вернетесь совсем другим человеком – если вернетесь…',
        0, 368, 0, 0, 500, '2023-06-07', '5af334dc-f3b2-457b-b092-89e4f0330406', 'Канун дня всех святых');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('One of Ray Bradbury’s classic noir novels, available in ebook for the first time.Toiling away amid the looming palm trees and decaying bungalows, a struggling young writer (who bears a resemblance to the author) spins fantastic stories from his fertile imagination upon his clacking typewriter. Trying not to miss his girlfriend (away studying in Mexico), the nameless writer steadily crafts his literary effort – until strange things begin happening around him.Starting with a series of peculiar phone calls, the writer then finds clumps of seaweed on his doorstep. But as the incidents escalate, his friends fall victim to a series of mysterious ‘accidents’ – some of them fatal. Aided by Elmo Crumley, a savvy, street-smart detective, and a reclusive actress of yesteryear with an intense hunger for life, the wordsmith sets out to find the connection between the bizarre events, and in doing so, uncovers the truth about his own creative abilities.',
        0, 379, 0, 0, 500, '2023-06-07', '2f5ef648-bd6e-460e-a3fb-e6ea6b2a235c', 'Death is a Lonely Business');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Вниманию читателя предлагается роман Рэя Брэдбери «Вино из одуванчиков», который можно назвать одним из самых «летних» произведений мировой литературы. Издание содержит адаптированный и сокращенный текст романа. Задача комментариев и словаря – помочь учащемуся понять текст. Роман делится на смысловые части – главы. Упражнения, предлагаемые после каждой главы, позволяют проверить понимание текста, дают возможность поразмышлять над вопросами и потренироваться в переводе отдельных конструкций, способствуют развитию навыков устной речи, служат отработке навыков чтения и произношения слов. Пособие адресовано учащимся старших классов, студентам, а также всем, кто изучает английский язык самостоятельно.',
        0, 369, 0, 0, 500, '2023-06-07', '06d4501c-ca32-4942-baad-d7a99ecfeeba',
        'Dandelion Wine / Вино из одуванчиков. Книга для чтения на английском языке');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Выйди ночью на улицу, посмотри в небеса на красную планету и скажи: «Марс, забери меня домой…»
Сначала они прибывали горстками. Затем сотнями. Миллионами. Они смахнули с поверхности Марса величавую, гибнущую цивилизацию, чтобы выстроить города и торговые центры. Марс стал воплощением безудержных грез. Превратился в новую Землю взамен старой – утопающей в войнах и мусоре. Стал чистым холстом для безумных причуд и самых мрачных наваждений человечества. И земляне, что прибыли покорять Красную планету, обнаружили, что Марс покорил их самих.',
        0, 370, 0, 0, 500, '2023-06-07', '782ddb75-7de1-428a-b0b9-b844d5fd0c06',
