INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Брэдбери всегда был и физиком, и лириком. Он создавал новые миры и предупреждал о последствиях технологического прогресса. Этот сборник откроет новые грани Великого Мастера: как автора пронзительной лирики, художника волшебного детства, демиурга целых городов.
Впервые изданные на русском рассказы и стихотворения дополняются редкими дневниковыми записями и воспоминаниями о других писателях.
Создана при участии ООО «Аудиокнига»', 0, 376, 0, 0, 500, '2023-06-07', 'a7ce978d-e371-450b-b3f3-bfcae0fd06fd',
