INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Одинокая пенсионерка Берта Владимировна наполняет свои дни разными делами, в том числе и добрыми. Всего-то хотела помочь людям, подписала дурацкий договор, и совсем не ожидала оказаться на королевском отборе невест. Без магии, без красоты и молодости, ей придется состязаться с другими невестами за руку красавца-мага. Пенсионерка не унывает, жизненный опыт и обширный багаж знаний позволяет ей не только удержаться на плаву, но и побеждать. А дальше вмешиваются маги, плетутся интриги, вместо привычной мирной жизни получается квест на выживание, да еще и с ребенком на руках!',
