INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Представляем аудиозапись, прочитанную актером театра им.Е.Вахтангова Андреем Зарецким, с шумовыми эффектами и оригинальным саундтреком.
<i>"Я, Хозе да Сильвестра, умирая от голода в маленькой пещере, пишу это собственной кровью в год 1590-й, обломком кости на клочке моей одежды. Пусть мой друг даст знать королю о том, что здесь написано, чтобы он мог послать сюда армию. Если она преодолеет пустыню и горы и сможет победить отважных кукуанов и их дьявольское колдовство, то он станет богатейшим королем со времен Соломона. Я видел собственными глазами несметное число алмазов в сокровищнице Соломона, за Белой Смертью, но из-за вероломства Гагулы, охотницы за колдунами, я ничего не смог унести и едва спас свою жизнь. Пусть тот, кто пойдет туда, следует по пути, указанному на карте, и восходит по снегам, лежащим на левой Груди Царицы Савской, пока не дойдет до самой ее вершины. Пусть он убьет Гагулу. Молитесь о моей душе. Прощайте. Хозе да Сильвестра" </i>',
