INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Юрий Иосифович Коваль – один из самых любимых детских писателей второй половины XX века у нас в стране, сценарист мультфильмов и детских фильмов, художник, автор и исполнитель собственных песен.
Пожалуй, самая известная книга Ю. Коваля – юмористическая детективная трилогия о приключениях Васи Куролесова и его пса Матроса, которые помогают милиционерам из города Карманова. Юные сыщики участвуют в весёлых, а порой даже опасных расследованиях, в погонях, перестрелках, поисках преступников и схватках с бандитами…
В первой части трилогии вам предстоит познакомиться с добрым и доверчивым пареньком Васей Куролесовым из деревни Сычи. Однажды поехал Вася в город Карманов купить поросят. А вернувшись домой, обнаружил, что черноусый продавец вместо поросят подсунул ему рыжего облезлого пса. Вася решил отыскать мошенника, но в результате сам попал в милицию. Тут-то он и познакомился со старшиной Таракановым и капитаном Болдыревым…',
        0, 493, 0, 0, 500, '2023-06-07', 'bf16fe00-7ca5-4182-a09c-bb731a80fb89', 'Приключения Васи Куролесова');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Он был простым английским парнем, но его объявили верховным богом ацтеков – Тескатлипокой. он был уверен, что станет лекарем и мирно проживет жизнь в отцовской усадьбе, но встал плечом к плечу с ацтекскими воинами в борьбе против захватчиков на руинах Теночтитлана. Он поклялся в верности девушке по имени Лили, но стал мужем индейской принцессы Отоми, чья любовь спасла его от смерти на жертвенном камне под ножом жреца…
Герой романа Томас Вингфилд отправляется в Испанию, чтобы отомстить убийце своей матери, но встречает его лишь десятилетия спустя на другом конце света, среди льда и огня возле жерла вулкана, прожив удивительную и трагическую жизнь и став свидетелем того, как под натиском испанских конкистадоров рухнула цивилизация ацтеков.
Глубокое знание автором истории и его талант рассказчика делают «Дочь Монтесумы» одним из самых блестящих произведений историко-приключенческого жанра.',
