INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'В этой аудиокниге вас ждет рассказ о фантастических приключениях барона Мюнхаузена – самого «правдивого» человека на свете. Основу этих невероятных историй составили рассказы действительно жившего в XVIII веке барона Мюнхгаузена. Он был военным, некоторое время служил в России, а вернувшись домой, в Германию, прославился как блестящий рассказчик, выдумывавший самые правдивые и увлекательные истории, якобы происходившие с ним на самом деле. Неизвестно, сам ли он впоследствии записал свои рассказы или это сделал кто-то другой, но в 1781 году некоторые из них были напечатаны, а еще через четыре года немецкий писатель Распэ издал их отдельной книгой.
