INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Книги французского писателя Мориса Леблана о приключениях Арсена Люпена раскупались при жизни автора огромными тиражами, превышающими тиражи книг самого Конан Дойла. Кто же такой этот Арсен Люпен?
Арсен Люпен – неуловимый грабитель, о подвигах которого трубят все газеты; таинственный персонаж, джентльмен с неистощимой фантазией, орудующий лишь в замках и великосветских гостиных. Арсен Люпен – человек с тысячью обличий. Поймать его невозможно, предсказать его следующий шаг – исключено. Его хитрость, его благородство, его изобретательность не знают границ. Разве что великий и непобедимый Херлок Шолмс примчится из окутанного туманами Лондона, чтобы справиться с самым известным преступником Франции…
В настоящее издание вошли четыре романа и сборник рассказов Леблана о приключениях Арсена Люпена. Впервые в России эти произведения сопровождаются иллюстрациями французских художников из первых оригинальных изданий.',
        0, 507, 0, 0, 500, '2023-06-07', '74170d63-f952-4aa0-8a96-dd64d2a08a06', 'Приключения Арсена Люпена');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Повесть Н. Н. Носова «Приключения Толи Клюквина» была написана более чем полвека назад, но по-прежнему остаётся очень современной. Сегодняшние мальчишки и девчонки, читая о жизни своих литературных сверстников, конечно же, узнают в них себя, понимают их радости, переживания и обиды и от души смеются над забавными случаями, происходящими с ними.',
