INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Кто не слышал про знаменитого барона Мюнхгаузена и его фантастические приключения! Чего только не случалось с этим храбрецом и выдумщиком! Довелось ему пролететь на пушечном ядре, с одного раза подстрелить семь куропаток, выбраться из брюха огромной рыбы и даже побывать на Луне! Правда это или ложь – решать читателю. Но сам барон Мюнхгаузен действительно существовал. Жил он в XVIII веке в Германии и был дворянином, военным и остроумным выдумщиком, прославившимся своими историями на весь свет. Его невероятные рассказы легли в основу книги немецкого писателя Эриха Распе, а также многих спектаклей, художественных и мультипликационных фильмов.
<br>
В формате PDF A4 сохранён издательский дизайн.', 0, 520, 0, 0, 500, '2023-06-07',
