INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Студия АРДИС предлагает вашему вниманию аудиокнигу «Приключения Карандаша и Самоделкина» – детскую повесть Юрия Дружкова о веселых и немного страшных приключениях Карандаша и Самоделкина, с которыми юные читатели знакомятся уже не одно десятилетие.
В небольшом, но очень красивом городе живут два маленьких человечка. Они волшебники. Одного зовут Карандаш и у него вместо носа волшебный карандаш. Всё что он нарисует своим носом – тут же оживает и превращается из нарисованного в настоящее. Второй – Самоделкин. Это маленький железный человечек, у которого волшебные руки. Он может за несколько секунд починить и отремонтировать всё что угодно: машину, вертолёт или кораблик.
Друзьям приходится спасаться от врагов – пирата и шпиона, неосторожно нарисованных Карандашом, но веселые человечки всегда выходят победителями из самых безнадежных ситуаций.',
        0, 521, 0, 0, 500, '2023-06-07', '8cb1a4ea-be7c-4885-9307-b369c5231dfe', 'Приключения Карандаша и Самоделкина');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'У Петечки Осликова есть мама, которая много читает и «делает важные дела», папа, который много работает и… работает, а также бабушка и дедушка&nbsp;– нормальные все люди. Один Петя какой-то неправильный, да что там неправильный&nbsp;– ужасный мальчик! Просто недоразумение какое-то!
Ну а Петечка Осликов (если, конечно, когда-нибудь кто-нибудь его об этом спросит) скажет вам совершенно искренне, что с этими взрослыми можно с ума сойти.
Елена Соковенина&nbsp;– писатель, журналист, блогер, финалист «Русской премии» (2010) и конкурса на лучшее произведение для детей и юношества «Книгуру» (2013, 2014), веселая и умная мама уже почти взрослого мальчика&nbsp;– большой специалист по детским проказам и взрослым переживаниям.',
