INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Вы не мечтали в детстве найти клад? Не хотели удрать из дома? И всегда слушали советы взрослых? Вы просто ничего не знаете о Томе Сойере?
