INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Кот Сократ так много путешествовал по свету: и&nbsp;в Австралии побывал, и в Африке. Да что там по свету – он даже в космос летал!
Но на этот раз он решил остаться на родине и совершенно случайно оказался в Кремле. Царь-пушка, Царь-колокол, Большой Кремлевский дворец – все они так знакомы и дороги сердцу каждого россиянина. Но как их увидел кот Сократ и что подумал? Конечно, без философствований не обошлось. Без них Сократ – не Сократ.',
        0, 535, 0, 0, 500, '2023-06-07', 'ed24a8f7-8866-43e9-bd98-a2511b540712', 'Приключения кота Сократа в Кремле');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'<b>Два полных авторских сборника о самом известном детективе.</b>
Артур Конан Дойл (1859–1930) – английский писатель, отдавший дань практически всем литературным жанрам, но наиболее известный как автор детективных, историко-приключенческих и фантастических произведений. И, конечно же, как создатель знаменитого тандема – Шерлока Холмса и доктора Уотсона. Книги о гениальном сыщике и его простоватом напарнике переведены практически на все языки мира, Холмс и Уотсон стали героями бесчисленных литературных подражаний, экранизаций и театральных постановок.
Прошло уже больше ста лет с того момента, как был напечатан первый рассказ, а читатели всего мира по-прежнему с упоением погружаются в мир туманных лондонских улиц, зловещих болот и вересковых пустошей.
В настоящем издании представлено полное собрание рассказов и повестей о великом сыщике с Бейкер-стрит.
