INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'<i>«Да здравствует Трансатлантический туннель! Ура!»</i>
В этом мире Америку открыл вовсе не Колумб, а испанцы в XIII веке сдались на милость мавров. Здесь самолеты летают на угле, а поезда работают на атомных двигателях. Цивилизация развивается хаотичными рывками, и вот начинается очередной грандиозный проект – строительство туннеля между Англией и Америкой!
<i>«Звездные похождения галактических рейнджеров»</i>
Два юных гения случайно открывают способ перемещения предметов за доли секунды на громадные расстояния. Для испытания чудесного устройства они используют самолет, принадлежащий футбольной команде колледжа. Русский шпион пытается угнать самолет, и внезапно герои оказываются в космосе, где сталкиваются с невероятным количеством различных представителей инопланетных рас!',
        0, 539, 0, 0, 500, '2023-06-07', 'bb9b0232-9047-41fd-b3b3-0844796a89cb',
        'Приключения на земле, под землей и в космосе');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Однажды мальчик Тим потерял аппетит, он не хотел есть ни овощи, ни котлеты, ни даже любимые макароны. Куда же пропал аппетит? Тим и его новые друзья – доктор Метаболиссио и собака Авокадо – отправляются на поиски в увлекательный мир клеток и питательных веществ.
Присоединяйтесь, и вы узнаете:
– зачем нашему организму белки, жиры, углеводы и витамины;
– какие витамины за что отвечают;
– как возникает голод и почему важно помнить про калории;
– чем полезен жир и когда вреден сахар;
– что не так с фастфудом;
– как выбирать полезные продукты в магазине;
– как правильно питаться и зачем нужно заниматься спортом.', 0, 540, 0, 0, 500, '2023-06-07',
        '87b9fd0b-1ada-4c7c-80b2-9298798087e2', 'Приключения Тима в поисках потерянного аппетита');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'В книгу Кира Булычёва «Приключения Алисы Селезнёвой» вошли шесть историй про Алису, написанные специально для младших школьников. По первой фантастической повести, вошедшей в книгу, был снят мультфильм «Тайна Третьей планеты». После успеха мультфильма Кир Булычёв переписал несколько историй про Алису Селезнёву. Именно эти повести и вошли в книгу.
Алиса отправится в путешествия на разные планеты, побывает даже в параллельных мирах и разгадает замыслы коварных космических пиратов. Алиса Селезнёва очень сообразительная девочка, и смекалка выручает её в разных ситуациях. В самых опасных приключениях важнее всего оказывается доброе сердце и верные друзья. Рисунки Екатерины Муратовой.
Для младшего школьного возраста.', 0, 541, 0, 0, 500, '2023-06-07', '22cca878-22bd-4a13-ba9a-cf7b02447900',
        'Приключения Алисы Селезнёвой');
