INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Герои Анатолия Рыбакова хорошо знакомы уже нескольким поколениям детей, любителей веселых и опасных приключений. Любознательный и честный Крош увлекается расследованием загадочных происшествий. Его волнует не только то, что произошло рядом с ним, но и то, что случилось за много лет до его рождения. В повести «Приключения Кроша» он пытается раскрыть кражу обыкновенных деталей от грузовика.',
        0, 545, 0, 0, 500, '2023-06-07', 'a01a2ae8-4a04-4d11-a30e-f651f1e3dae2', 'Приключения Кроша');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Сын киммерийского кузнеца, Конан-варвар родился на свет на поле боя! Он был вором и наемником, искателем приключений и охотником за сокровищами, пока его не поманила к себе золотая корона Аквилонии… Силой захватив престол, он вскоре сам был низвергнут с трона черным колдовством. Враги Конана воскресили могущественного мага древности, которого он может победить, лишь вернув сердце своего королевства – таинственный талисман Сердце Аримана…',
