INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Владимир Алеников – известный писатель, кинорежиссёр и поэт-переводчик. Его книги о Петрове и Васечкине, а также снятые на их основе фильмы давно стали классикой, любимой детьми нескольких поколений.
