INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Книга «Приключения Пиноккио» Карло Коллоди уже давно стала национальным достоянием Италии, и её тиражи занимают лидирующие строчки не только среди детских, но и среди взрослых книг, а сюжет сказки лёг в основу многих художественных фильмов. Это история деревянной куклы, сделанной из говорящего полена, которая научилась быть человеком. За детским сюжетом угадывается сложная проблема взросления, а сказочные обстоятельства приключений Пиноккио, его поведение, встречи и непростые решения научат читателя принимать ответственность за поступки, отличать правду от лжи. Чтение этой великолепной книги неизменно приносит радость детям и взрослым.',
