INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Автор бестселлеров «Тамплиеры» и «Плантагенеты» рассказывает об одной из самых захватывающих и трагических глав британской истории. В XV веке страна пережила череду длительных и кровопролитных гражданских вой­н.
Корона Англии семь раз переходила из рук в руки, пока представители знатных родов боролись за право на власть.
Дэн Джонс завершает свою эпическую историю средневековой Британии книгой о Вой­не Алой и Белой розы и показывает, как Тюдоры разгромили Плантагенетов и заполучили корону. Он ярко описывает блеск королевского двора и постигшие страну бедствия, интриги и заговоры, а также знаменитые сражения – и среди них битву при Таутоне, в которой погибло 28000 человек, и при Босворте, где в бою пал последний король из династии Плантагенетов.
Это реальные события, стоящие за знаменитыми историческими хрониками Шекспира, а также популярным сериалом Би-би-си и послужившие основой «Игры престолов».',
        0, 630, 0, 0, 500, '2023-06-07', 'b5de0c02-cb18-4553-950c-cfea1a37a984',
        'Война Алой и Белой розы: Крах Плантагенетов и воцарение Тюдоров');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Слова, вынесенные в название книги,&nbsp;– это не эмоциональное преувеличение автора. «Война на уничтожение»&nbsp;– так охарактеризовал будущую войну против СССР сам Адольф Гитлер. Попытка доказать, что фюрер готовил только разгром коммунизма, а народам России желал свободы и процветания, лукава и научно несостоятельна.
Множество документов Третьего рейха вполне ясно говорит о том, что нацисты стремились завоевать жизненное пространство за счет советских территорий, навсегда уничтожить российское государство в Европе и ослабить славянскую биологическую силу настолько, чтобы она уже никогда не могла оказать сопротивление германским народам.
Аналитики нацистов даже заблаговременно высчитали, сколько советских граждан должны умереть для благоденствия Великой Германии. Выжившим они отводили роль невольников немецкого господина. Зловещие планы германских элит могли бы стать явью, если бы не беспримерное сопротивление народов Советского Союза.',
        0, 631, 0, 0, 500, '2023-06-07', '2cf78b4d-7e73-4ded-b1e1-596566ba41e6',
        'Война на уничтожение. Что готовил Третий рейх для России');
