INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Что такое патриотизм: эмоция или идеология? Если это чувство, то что составляет его основу: любовь или ненависть, гордость или стыд? Если идеология, то какова она – консервативная или революционная; на поддержку кого или чего она ориентирована: власти, нации, класса, государства или общества? В своей книге Владислав Аксенов на обширном материале XIX – начала XX века анализирует идейные дискуссии и эмоциональные регистры разных социальных групп, развязавших «войну патриотизмов» в попытках присвоить себе Отечество. В этой войне агрессивная патриотическая пропаганда конструировала образы внешних и внутренних врагов и подчиняла политику эмоциям, в результате чего такие абстрактные категории, как «национальная честь и достоинство», становились факторами международных отношений и толкали страны к мировой войне. Автор показывает всю противоречивость этого исторического феномена, цикличность патриотических дебатов и кризисы, к которым они приводят. Владислав Аксенов – доктор исторических наук, старший научный сотрудник Института российской истории РАН, автор множества работ по истории России рубежа XIX–XX веков.',
        0, 633, 0, 0, 500, '2023-06-07', '958dc284-2127-4dbe-b3f0-b303caf4faa9',
        'Война патриотизмов: Пропаганда и массовые настроения в России периода крушения империи');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'История некроманта заканчивается. Заканчивается масштабно, с неожиданными поворотами в последний момент, с жертвами и с грустью.
На затерянном среди морей Эвиала островке Утонувший Краб пересекутся наконец десятки историй и сюжетных линий. В войне за судьбы миров и всего Упорядоченного сойдутся все силы. Бог встанет плечом к плечу со смертным, тёмный эльф со светлым, некромант с инквизитором.
А победить сможет даже не тот, кто обладает Мечами, из-за которых и разразилась эта борьба, а тот, кто обладает силой воли и способностью принимать нелегкие решения.',
        0, 634, 0, 0, 500, '2023-06-07', 'aabd759b-7cbb-4463-b9f4-d20e072c6320',
        'Война мага. Том 4. Конец игры. Часть 2');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Страшная война между Светлыми и Тёмными силами началась под неприступными стенами Чёрной Башни, к которым стянута многотысячная армия отца-экзекутора Этлау. Магические бои разворачиваются со всей мощью&nbsp;– против Тьмы выступают чародеи и некромант. Здесь и сейчас решается судьба Упорядоченного. А на всех других землях бушуют обычные войны&nbsp;– на копьях и мечах,&nbsp;– и не видно им конца. Борьба добра и зла не прекращается ни на минуту, и не всегда понятно, где на самом деле добро, а где зло.
Однако Фессу удаётся вырваться из окружения и расстроить планы Западной Тьмы…', 0, 635, 0, 0, 500, '2023-06-07',
        'c78f785b-1f3f-41d5-96a6-c0338882f5db', 'Война мага. Том 1. Дебют');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Девятый и заключительный том серии «Рандеву с Варягом». Отгремели последние раскаты взрыва гигантского метеора, к которому навсегда теперь прилипло наименование «Лондонский». Мир восхищен, озадачен и испуган. Четыре года русский император Михаил скрывал от всех огромную мощь, способную разрушать царства и уничтожать целые армии, а применил ее для того, чтобы спасти от уничтожения столицу своего злейшего врага.
Тем временем сражения в Македонии, по причине полного поражения турецкой армии, почти затихли, и болгары перебрасывают дивизии во Фракию для действий на Константинопольском направлении. Русский Черноморский флот и армия готовы прийти к ним на помощь в решающей схватке. Людоедский режим Оттоманской Порты, виновник множества кровавых преступлений, должен быть разрушен. Начинается основная фаза Войны за Проливы. А для остальной Европы пришло время Великих Перемен. Фигуры на европейской шахматной доске расставлены, все ритуальные слова произнесены – и теперь осталось только сделать первый ход.',
        0, 636, 0, 0, 500, '2023-06-07', '7c64ee64-37c7-445b-bbf5-f8efac665ea7', 'Война за Проливы. Решающий удар');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'За Эвиал сражаются слишком многие великие силы. Но последователи учения Тёмного Мага Эвенгара Салладорского решили призвать в этот мир ещё одну&nbsp;– самого Салладорца! Для его возрождения из мёртвых они проводят кровавые обряды в пустыне…
Некромант и девочка-дракон Рыся, которая стала ему приёмной дочерью, отправляются в путь, чтобы помешать им.', 0, 637,
