INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Ещё недавно злейшие враги, воевавшие друг против друга у Чёрной Башни, некромант Неясыть и инквизитор Этлау оказываются по одну сторону баррикад в борьбе за Аркин, Святой город. Им приходится на время забыть прошлые разногласия и объединиться против нашествия Империи Клешней, иначе может не оказаться ни правых, ни виноватых.
Спасать Эвиал им помогают маги Ордосской Академии и Волшебный Двор, Клара Хюммель, Сильвия и Динтра, а также непримиримые соперники&nbsp;– эльфы Нарна и Вечного леса. Только вместе они могут отстоять само существование жизни…',
