INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Ритуал хитрого божка сковал силы Падших и Отверженных. Борьба между божественными сущностями на время затихла. Пользуясь слабостью бывших богов, армии Великого Дуэгара взяли в осаду Шасцу-Насхар – город тысячи храмов, обитель конклава первосвященников и воинских орденов. Тем временем Эрик дэс’Сарион пытается отдалиться от противостояния могущественных сущностей, явившихся из божественного Эфира, и пробует устроить себе мирную жизнь. Он еще не знает, что Отверженные больше не считают его союзником и уже отправили по его следу своих безжалостных гончих. Юного Алхимика ждет очередной виток новой войны. Публикуется в авторской редакции с сохранением авторских орфографии и пунктуации.',
        0, 640, 0, 0, 500, '2023-06-07', '78c4c1dc-107b-43e3-88ae-f7b3d5a6a955', 'Война Алхимика');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Восьмой том серии «Рандеву с Варягом». В войне за Проливы сделаны первые шаги и одержана первая бескровная победа, достигнутая дипломатами и агентами ГУГБ. Сменив князя Болгарии со старого Фердинанда на юного Бориса Третьего, Российская Империя царя Михаила вплотную приблизилась к Константинополю – только протяни руку и возьми. Но не все так просто. Несмотря на то, что Османская империя чрезвычайно дряхла, при ее убиении нужно соблюдать технику безопасности. Сначала необходимо создать условия для того, чтобы в бойню за Проливы не втянулась половина Европы, а уже потом делать в войне первый выстрел.',
        0, 641, 0, 0, 500, '2023-06-07', '7436eedc-15c5-4c9b-8526-d178a7113da5',
        'Война за проливы. Операция прикрытия');
