INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Седьмой том серии «Рандеву с Варягом». В мире царя Михаила относительно спокойно прошли три года. Победа в русско-японской войне и новый государь укрепили Российскую империю, но впереди – Первая Мировая война. Что должны сделать царь-реформатор и его помощники, чтобы избежать изнуряющей войны и превратить поражение в победу? Об этом читайте в этой и следующих книгах трилогии «Война за Проливы».',
        0, 644, 0, 0, 500, '2023-06-07', 'dcb552c3-812a-4438-b93c-da431f8a7771', 'Война за проливы. Призыв к походу');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Нет никого страшнее людей, готовых нести счастье всем, даром и чтоб никто не ушёл обиженным – особенно если эти люди владеют высокой магией. И вот уже знаменитая Гильдия Боевых Магов отступает и несёт потери, горят миры и рушатся крепости, а Долина Магов оказывается в опасности. Что поможет победить, переломить волю фанатиков? Чародейка Клара Хюммель считает, что оружие, валькирия Райна – честь, Ричард д’Ассини – хитрость, а ловец магических тварей Эварха – здравый смысл… Однако, так или иначе, бой приходится принять, и Гильдия стоит насмерть. Ловец Эварха, волей судьбы оказавшийся в самой гуще событий, чувствует за всем этим чью-то злую волю – только вот чью?
Финальная битва Войны ангелов ответит на все вопросы!', 0, 645, 0, 0, 500, '2023-06-07',
        '5262349b-21e6-45bb-831e-f026107123e4', 'Война ангелов. Игнис');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Перед вами игра, эксперимент, литературное хулиганство, из которого получился актуальный роман о сегодняшней России на 24 писательских голоса. Авторы – известные и начинающие, мастера разных жанров и направлений, разных поколений и убеждений – написали роман-буриме. Денис Драгунский запустил интригу, Сергей Лукьяненко, Григорий Служитель, Алексей Сальников и многие другие развивали сюжет, а Дмитрий Быков в эпилоге подвел итоги – и все перевернул с ног на голову. Начинаясь как роман «young adult» с&nbsp;героями-десятиклассниками, история превращается в фантасмагорию, абсурд, кривое, не по-детски страшное зеркало нашей реальности.',
        0, 646, 0, 0, 500, '2023-06-07', '2d016592-e648-4952-b5a0-f93310d47706', 'Война и мир в отдельно взятой школе');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'«Война хитрожопых» – фантастический роман Дмитрия Дорничева, шестая книга цикла «Моё пространственное убежище», жанр ЛитРПГ, постапокалипсис.
