INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('Автор этой книги, известный писатель Армен Гаспарян, обращается к непростой теме – возрождению нацизма и национализма на постсоветском пространстве. В чем заключаются корни такого явления? В том, что молодое поколение не знало войны? В напряженных отношениях между народами? Или это кому-то очень выгодно? Хочешь знать будущее – загляни в прошлое. Но как быть, если и прошлое оказывается непредсказуемым, перевираемым на все лады современными пропагандистами и политиками? Армен Гаспарян решил познакомить читателей, особенно молодых, с историей власовского и бандеровского движений, а также с современными продолжателями их дела. По мнению автора, их история только тогда станет окончательно прошлым, когда мы ее изучим и извлечем из этого уроки. Пока этого не произойдет, это будет не прошлое, а наша действительность. Посмотрите на то, что происходит на Украине.',
        0, 650, 0, 0, 500, '2023-06-07', 'c33d5f8f-9304-4ab3-8a89-781abb2a43cd',
        'Война после Победы. Бандера и Власов: приговор без срока давности');
