INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Эдит Ева Эгер в юном возрасте была отправлена вместе со своей семьей в&nbsp;Аушвиц. Там ее родители погибли в газовой камере, а&nbsp;Эдит и ее сестра испытали на себе все ужасы отвратительных лагерей смерти. Через 35 лет после окончания войны, став известным психологом, Эдит вернулась в&nbsp;Аушвиц, чтобы избавиться от воспоминаний о прошлом и вины выжившего.
