INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'«Война ведьм» – фантастический роман Дмитрия Нелина, шестая книга цикла «Охотник на читеров», жанр LitRPG, боевое фэнтези.
