INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('И у Империи Кленнон, и у Демократического Альянса людей, и у Гегемонии ящеров-скаари были свои причины начать войну – причины достаточно разные, но своей критической массы они достигли практически одновременно. Небольшая вспышка, произошедшая от отдаленной космической станции, зажгла пламя, в котором запросто может сгореть вся галактика. Ведь если раньше люди убивали людей, скаари убивали скаари, а кленнонцы – кленнонцев, теперь у них появилась прекрасная возможность убивать всех подряд…',
