INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Неспокойные времена грядут в благословенной Долине магов. Гильдия боевых магов достигла своего расцвета: заказы из разных миров сыплются один за другим, другие Гильдии требуют сопровождения в путешествиях, а боевых магов на всех не хватает. Однако законы Долины, установленные самими Основателями, запрещают любое расширение. Тогда Ричард д’Ассини, глава Гильдии, призывает изменить закон. Так Клара Хюммель, боевой маг по найму, попадает меж двух огней – в конфликт между своей Гильдией и Архимагом Долины Игнациусом Коппером.
А в это время далеко отсюда молодой ловец магических тварей по имени Эварха получает крайне необычный заказ.
И никому невдомек, что эти события тесно связаны, и приведут они к настоящей катастрофе…', 0, 657, 0, 0, 500,
