INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES ('В&nbsp;1877—1878&nbsp;годах Россия на&nbsp;кончиках штыков принесла свободу балканским славянам. Эта война вошла и&nbsp;в&nbsp;нашу историю, и&nbsp;в&nbsp;нашу культуру: Плевна и&nbsp;Шипка, Скобелев и&nbsp;Гурко, «Баязет» и&nbsp;«Турецкий гамбит». Эта война вышла кровопролитной, динамичной и&nbsp;осталась необычайно ярким и&nbsp;драматическим сюжетом нашей истории.',
