INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Летом 1942 года двадцатидвухлетняя Фрэнси Рабинек прибыла в Терезин, концентрационный лагерь и гетто в сорока милях к северу от своего дома в Праге. Это было началом ее трехлетнего путешествия из Терезина в чешский семейный лагерь в Освенциме-Биркенау, в лагеря рабовладельческого труда в Гамбурге и Берген-Бельзене. После освобождения англичанами в апреле 1945 года она наконец вернулась в Прагу.
Война Фрэнси – это обескураживающая история невероятно сильной молодой женщины, которая пережила ужасы Холокоста и выжила.',
        0, 683, 0, 0, 500, '2023-06-08', '1a79ae3c-5638-434c-b777-e1750033ba96', 'Война Фрэнси');
INSERT INTO books (description, discount, image_id, is_bestseller, popularity, price, pub_date, slug, title)
VALUES (e'Война Кланов – фантастический роман Бориса Романовского, четвертая книга цикла «Андрей», жанр постапокалипсис, РеалРПГ, боевая фантастика.
Пока я прохлаждался в Африке, на родине произошло множество событий. Зомби создали своё поселение рядом с моим городом, а ближайшие людские города объединились в Союз и точат клинки. В это же время внутри Архитекторов предатели плетут интриги, а из Китая неизбежно надвигается бесчисленная орда зомби. И это лишь часть проблем, которые мне предстоит решить после моего возвращения домой.
