update books
set image_id = 1
where id in (select b.id
             from books b
                      left join image i on b.image_id = i.id
             where i.size = 0);

update authors
set image_id = 1
where id in (select a.id
             from authors a
                      left join image i on a.image_id = i.id
             where i.size = 0
                or a.id isnull);
